import { Entity, Column, PrimaryGeneratedColumn } from "typeorm";

@Entity("Inventory")
export class InventoryEntity {
  @PrimaryGeneratedColumn()
  id: number;

  @Column("text",{nullable: true})
productId: string;

@Column("integer",{nullable: true})
quantity: number;

@Column("integer",{nullable: true})
lowStockThreshold: number;


}
